#pragma once#include <string>#include <functional>#include <vector>#include "iTestObject.h"#include "../src/pipelineHandlers/pipelineHolder.h"namespace benchmark{//! @class Benchmaker makes tests routine measuring average time for testclass Benchmaker{public:    Benchmaker();    ~Benchmaker();    //! @arg count shows how much repeats of single test will be done to get average value    void setRoundsCount(unsigned int const &count = 10);    //! @arg object is a subject that will be tested    void setRunnableObject(ITestObject *object);    typedef std::function<std::pair<bool, int> (void)>  ParamGenerator;    void setTestingParam(int const x);    // loop with @attention increasing params    //! @arg from is start param "N"    //! @arg to   is max bound of tests    //! @arg step is a positive difference between each test    void setTestingParam(int from, int to, int step = 1);    void setTestingParam(std::initializer_list<int> list);    //! returns (true, N) as next testing param or (false, .) if there are no more testing params    void setTestingParam(ParamGenerator paramYielder);    //! start benchmark on given object    void makeBenchmark();    //! @arg mustLog switches mode of logging to file    void setLogginToFile(FileOutput const format);    //! @arg name is prefix of filename with tests result    //! must be called After setting new runnable object    void setBenchmarkName(std::string const &name);private:    FileOutput mFileFormat;    int mRoundsCount;    std::string mBenchmarkName;    std::string mResultsFilename;    ITestObject *mTestObj;    benchmark::impl::PipelineHolder mPipeline;    ParamGenerator mParamGenerator;    std::vector<int> mParamList; // could be unused    //! makes preparation before- and cleaning after test    //! @return time per test in msec    long makeRound(int const &paramN);    //! runs single test    long makeTest();    void configureBenchmark();    void freeTestObject();    std::string getTimeString();};} // benchmark